import React, {Component} from 'react';
import CardList from './CardList';
import './App.css';
import SearchBar from './SearchBar'
import {list} from './list';
import PageDetail from './PageDetail';
import {BrowserRouter as Router, Route} from 'react-router-dom';

class App extends Component{
	constructor(){
		super();
		this.state = {
			list: list,
			searchField: '',
			mouseData: [],
			height:'',
			width:'',
			keyBoardData:[]
		}
		this.updateWindowDimensions = this.updateWindowDimensions.bind(this);
		this.dwellTimes = [];
		this.flightTime = 0;
		this.temp = 0;
	}
	componentDidMount() {
		this.updateWindowDimensions();
		window.addEventListener("resize", this.updateWindowDimensions);
	}
	componentWillUnmount() {
		window.removeEventListener("resize", this.updateWindowDimensions)
	}
	updateWindowDimensions() {
		this.setState({width:window.innerWidth, height:window.innerHeight});
	}
	onSearchChange = (event) => {
		this.setState({searchField: event.target.value});
	}
	onMouseMove(e) {
		this.setState({mouseData: [...this.state.mouseData, {x: e.clientX, y: e.clientY, time: Date.now(),width: this.state.width, height: this.state.height}]});
		console.log(this.state.keyBoardData);
	}
	onKeyDown(e) {
		if (!this.dwellTimes[e.which]){
		    this.dwellTimes[e.which] = new Date().getTime();
		    if(this.temp !== 0){
			    this.flightTime = this.dwellTimes[e.which] - this.temp;
		    }
		    this.temp = this.dwellTimes[e.which];
		}
	}
	onKeyUp(e) {
		let dwellTime = new Date().getTime() - this.dwellTimes[e.which];
		this.setState({keyBoardData: [...this.state.keyBoardData, {key: e.key, flightTime: this.flightTime, dwellTime: dwellTime }]});
		delete this.dwellTimes[e.which];
	}	
	onMouseClick(e){
		console.log(e.clientX. e.clientY);
	}	
	getIP = () => {
		// fetch('http://137.30.213.8:3001/', {
		// 	method: 'get',
		// 	headers: {'Content-Type': 'application/json'},
		// })
		// .then(response => response.json())
	// console.log('hello');
	}
	render(){
		this.getIP();
		const filteredList = this.state.list.filter(filteredItem => {
			return filteredItem.name.toLowerCase().includes(this.state.searchField.toLowerCase());
		})
		return(
			<div className = 'app' onClick={this.onMouseClick.bind(this)} onMouseMove={this.onMouseMove.bind(this)} onKeyDown={this.onKeyDown.bind(this)}
				 onKeyUp={this.onKeyUp.bind(this)}>
			<div className = 'nav'>
			<h1>STATE OF JAVASCRIPT</h1>
			<div className='search'><SearchBar searchChange = {this.onSearchChange}/></div> 
			</div>
			<Router>
			<Route path="/" exact render = {() => <CardList list = {filteredList} />} />	
			<Route path="/:id" exact component={PageDetail}/>					
			</Router>
			</div>
			);
	}
}
export default App;
