import React, {Component} from 'react';
import CardList from './CardList';
import './App.css';
import SearchBar from './SearchBar'
import {list} from './list';

class App extends Component{
	constructor(){
		super();
		this.state = {
			listArray: list,
			searchField: ''
		}
	}

	onSearchChange(event){
		console.log(event);
	}

	render(){
		return(
			<div>
				<h1>RoboFriends</h1>
				<SearchBar searchField = {this.onSearchChange}/>
				<CardList list = {this.state.listArray} />
			</div>
			)
	}
}
export default App;
