import React, {Component} from 'react';
import CardList from './CardList';
import './App.css';
import SearchBar from './SearchBar'
import {list} from './list';
import Scroll from './Scroll';
import CardDetails from './CardDetails';
import Summary from './Summary';
import {BrowserRouter as Router, Route} from 'react-router-dom';

class App extends Component{
	constructor(){
		super();
		this.state = {
			list: list,
			searchField: ''
		}
	}

	onSearchChange = (event) => {
		this.setState({searchField: event.target.value});
	}
		
	getinfo = () => {
		fetch('http://137.30.213.8:3001/', {
			method: 'get',
			headers: {'Content-Type': 'application/json'},
		})
		.then(response => response.json())
		console.log('hello');
	}


	render(){
		this.getinfo();
		const filteredList = this.state.list.filter(filteredItem => {
			return filteredItem.name.toLowerCase().includes(this.state.searchField.toLowerCase());
		})
		return(
			<div className = 'app'>
				<div className = 'nav'>
				<h1>BIG MOVIES</h1>
				<div className='search'><SearchBar searchChange = {this.onSearchChange}/></div> 
				</div>
				<Router>
					<Route path="/" exact render = {() => <Scroll><CardList list = {filteredList} /></Scroll>} />	
					<Route path="/:id" exact component={CardDetails} />
					<Route path="/:id" exact component={Summary}/>
				</Router>			
			</div>
		);
	}
}
export default App;
