import React, {Component} from 'react';
import CardList from './CardList';
import './App.css';
import SearchBar from './SearchBar'
import {list} from './list';
import Scroll from './Scroll';
import CardDetails from './CardDetails';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';

class App extends Component{
	constructor(){
		super();
		this.state = {
			list: list,
			searchField: ''
		}
	}

	onSearchChange = (event) => {
		this.setState({searchField: event.target.value});
	}
			
	render(){
		const filteredList = this.state.list.filter(filteredItem => {
			return filteredItem.name.toLowerCase().includes(this.state.searchField.toLowerCase());
		})
		return(
			<div className = 'app'>
				<div className = 'nav'>
				<h1>BIG MOVIES</h1>
				<SearchBar searchChange = {this.onSearchChange}/> 
				</div>
				<Router>
					<Switch>
						<Route path="/" exact render = {() => <Scroll><CardList list = {filteredList} /></Scroll>} />	
					<Route path="/:id" exact component={CardDetails} />
					</Switch>
				</Router>			
			</div>
		);
	}
}
export default App;
